INSERT INTO `engine4_experts_experts` VALUES ('73', '', null, 'Sales Manager', '8', 'Hotel Link Solutions', 'Andy đã có trên 8 năm kinh nghiệm trong ngành kinh doanh khách sạn trong đó 5 năm làm sales các thị trường du lịch, thương mại, online. Đặt biệt 3 năm chuyên sâu vào sales online. Hiện Andy đang làm việc tại Neways Vietnam liên doanh nước ngoài về online marketing, giải pháp kinh doanh online cũng như tư vấn kinh doanh khách sạn.', '30276', '1219', null, '2013-05-15 03:26:27', '0');
