INSERT INTO `engine4_library_comments` VALUES ('42', 'woa!! sách hay để tham khảo về chính sách phát triển du lịch của Hawaii đây!!! mọi người cùng vào diễn đàn trao đổi nhé!', '211', '2012-05-23 07:38:09', '131', '1');
